package javaPractice.ch_01;

public class Exam05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*비교 연산자.
		 * 비교를 해서 참인지 거짓인지 확인하는게 목적.
		 * 결과 값은 true or false.*/
		
		int a = 10;
		int b = 20;
		int c = 30;
		System.out.println(a < b); // t
		System.out.println(a > b); // f
		System.out.println(a + b <= c); // t
		System.out.println(a + b >= c); // t
	}

}
